CREATE TABLE ORDERBEANTABLE (
    ACTUAL_COMPLETION_DATE TIMESTAMP ,
    API_CLIENT_ID VARCHAR(256) ,
    DESCRIPTION VARCHAR(256) ,
    LAST_MODIFIED TIMESTAMP ,
    MEV_PRIMARY_KEY VARCHAR(256) NOT NULL ,
    MEV_TYPE VARCHAR(256) NOT NULL ,
    ORDER_DATE TIMESTAMP ,
    PRIORITY INTEGER NOT NULL ,
    PURCHASE_ORDER VARCHAR(256) ,
    REQUESTED_COMPLETION_DATE TIMESTAMP ,
    STATE VARCHAR(256),
    CONSTRAINT ORDER_PK Primary Key (
      MEV_PRIMARY_KEY
    )
);

CREATE TABLE SERVICEBEANTABLE (
    LAST_MODIFIED TIMESTAMP ,
    MEV_PRIMARY_KEY VARCHAR(256) NOT NULL ,
    MEV_TYPE VARCHAR(256) NOT NULL ,
    ORDER_MEV_PRIMARY_KEY VARCHAR(256) NOT NULL ,
    POSITION_IN_ORDER_VALUE_ARRAY INTEGER NOT NULL ,
    STATE VARCHAR(256) ,
    SERVICE_ACTIVATOR_HOME_JNDI_NAME VARCHAR(256) ,
    SUBSCRIBER_ID VARCHAR(256) ,
    ADDITIONAL_ATTRIBUTES BLOB(10k) ,
    FAILED BOOLEAN NOT NULL,
    FAILED_EXCEPTION BLOB(10k) ,
    CONSTRAINT SERVICE_PK Primary Key (
      MEV_PRIMARY_KEY,
      ORDER_MEV_PRIMARY_KEY
    ),

    CONSTRAINT ORDER_FOREIGN_KEY Foreign Key (
      ORDER_MEV_PRIMARY_KEY
    )
    REFERENCES ORDERBEANTABLE (
      MEV_PRIMARY_KEY
    )

);


CREATE TABLE UNIQUEKEY (
      SEQUENCE BIGINT DEFAULT 0
);


COMMIT;
